# For 'python3 -m atlastk' to work.

from Atlas import *

if __name__ == "__main__":
  HEAD = """
  <title>"Hello, World !" example</title>
  <link rel="icon" type="image/png" href="data:image/png;base64," />
  """

  BODY = """
  <fieldset>
    <input id="input" maxlength="20" placeholder="Enter a name here" type="text"
          xdh:onevent="Submit" value="World"/>
    <div style="display: flex; justify-content: space-around; margin: 5px auto auto auto;">
    <button xdh:onevent="Submit">Submit</button>
    <button xdh:onevent="Clear">Clear</button>
    </div>
  </fieldset>
  """

  def ac_connect(dom):
    dom.inner("", BODY )
    dom.focus( "input")

  def ac_submit(dom):
    dom.alert("Hello, {}!".format(dom.get_value("input")))
    dom.focus( "input")

  def ac_clear(dom):
    if dom.confirm("Are you sure?"):
      dom.set_value("input", "" )
    dom.focus( "input")

  callbacks = {
    "": ac_connect,
    "Submit": ac_submit,
    "Clear": ac_clear,
  }

  launch(callbacks, None, HEAD)